mapper.update:
UPDATE admin SET 
	admin_pw = #{adminPw},
	admin_name = #{adminName}
WHERE admin_no = #{adminNo}

mapper.select:
SELECT a.admin_no, a.admin_id , a.admin_name, ap.admin_position, ap.admin_level, a.update_date FROM admin AS a LEFT JOIN admin_position AS ap ON a.admin_position_no = ap.admin_position_no
admin_position = #{adminPosition}
ORDER BY a.update_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT member_no, member_id, member_pw, member_name, member_age, member_tel, member_email, member_active, update_date FROM member
member_active = #{memberActive}
ORDER by update_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT host_no, host_id, host_pw, host_name, host_tel, host_active, update_date FROM host
host_active = #{hostActive}
ORDER by update_date DESC LIMIT #{beginRow}, #{rowPerPage}
