mapper.select:
SELECT q.qna_no, q.member_no, q.qna_category, q.qna_title, q.qna_secret, q.create_date, m.member_name, qc.admin_no FROM qna q LEFT JOIN member m ON q.member_no = m.member_no LEFT JOIN qna_comment qc ON q.qna_no = qc.qna_no
q.member_no = #{memberNo}
AND qna_category = #{qnaCategory}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT 
	COUNT(*) 
FROM qna

	member_no = #{memberNo}

		AND
		qna_category = #{qnaCategory}

SELECT m.member_no, m.member_name, ac.accom_name, ac.accom_no, r.room_name, rev.review_no, rev.review_content, rev.review_rank, rev.review_recommend, rev.create_date FROM member m JOIN reserve re ON re.member_no = m.member_no JOIN payment p ON re.reserve_no = p.reserve_no JOIN review rev ON rev.payment_no = p.payment_no JOIN room r ON r.room_no = re.room_no JOIN accom ac ON r.accom_no = ac.accom_no
m.member_no = #{memberNo} AND rev.review_recommend = #{reviewRecommend}
m.member_no = #{memberNo}
ORDER BY rev.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT re.reserve_no, ac.accom_name, p.payment_price, re.reserve_use FROM reserve re JOIN payment p ON p.reserve_no = re.reserve_no JOIN member m ON re.member_no = m.member_no JOIN room r ON r.room_no = re.room_no JOIN accom ac ON r.accom_no = ac.accom_no
m.member_no = #{memberNo} AND reserve_use = #{reserveUse}
m.member_no = #{memberNo}

SELECT c.complain_no, c.complain_category, c.complain_title, c.create_date, m.member_name, a.accom_name FROM complain c JOIN payment p ON c.payment_no = p.payment_no JOIN reserve r ON p.reserve_no = r.reserve_no JOIN room ro ON ro.room_no = r.room_no JOIN accom a ON a.accom_no = ro.accom_no JOIN member m ON r.member_no = m.member_no
m.member_no = #{memberNo} AND complain_category = #{complainCategory}
m.member_no = #{memberNo}

SELECT COUNT(*) 
FROM member 
WHERE 
	member_id = #{memberId}
	AND 
	member_certifycode = #{memberCertifycode}

SELECT member_active 
FROM member
WHERE member_id = #{memberId}

SELECT COUNT(member_id)
FROM member
WHERE member_no = #{memberNo} AND member_pw = #{memberPw}

SELECT COUNT(member_id)
FROM (
	SELECT member_id FROM member WHERE member_id = #{memberId}
	UNION
	SELECT host_id member_id FROM host WHERE host_id = #{memberId}
	UNION
	SELECT admin_id member_id FROM admin WHERE admin_id = #{memberId}
	UNION
	SELECT member_id FROM member_out WHERE member_id = #{memberId}
) AS u
