mapper.delete:
DELETE 
FROM notice 
WHERE notice_no = #{noticeNo} AND admin_no = #{adminNo}

mapper.select:
SELECT n.notice_no, n.notice_category, n.notice_title, n.create_date, a.admin_name FROM notice n LEFT JOIN admin a ON n.admin_no = a.admin_no
notice_category = #{noticeCategory}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}
