mapper.delete:
DELETE 
FROM qna_comment 
WHERE qna_no = #{qnaNo} AND admin_no = #{adminNo}

DELETE 
FROM qna 
WHERE qna_no = #{qnaNo} AND member_no = #{memberNo}

mapper.select:
SELECT q.qna_no, q.member_no, q.qna_category, q.qna_title, q.qna_secret, q.create_date, m.member_name, qc.admin_no FROM qna q LEFT JOIN member m ON q.member_no = m.member_no LEFT JOIN qna_comment qc ON q.qna_no = qc.qna_no
qna_category = #{qnaCategory}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}
